    fetch(`http://localhost:2020/login`)
    .then((response) => {
    response.json()
    .then(json =>{

        if(json.logged === true){
          setRole(json.foundUser.role)
}
    })
})


},[])

console.log(role);
  return (   <section >


  {role == "user" &&(     
    <Router>
   <Header/>





<Routes>


<Route  path="/" element={[<Button/>,<HomeFetch/>,<HomeRatedFetch/>,<HomeUpcommingFetch/>]}/>  
