import React, { Component } from 'react'
import Home from './Home'

class HomeFetch extends Component {
    constructor(props) {
        super(props);
        this.state = {data:[],
                    dataNew:[]

        }
    }



componentDidMount(){


        fetch(`http://localhost:2020/login`)
        .then(response => response.json())
        .then (json => this.setState({data: json }))


        }
        left=()=>{
            document.querySelector(".sectionGrid").scrollLeft-=500
        }
        right=()=>{
            document.querySelector(".sectionGrid").scrollLeft+=500
        }

        render() { 

        return ( <div className="h2Section">

            <h2>Beliebteste Filme</h2>
            <div className="sectionGrid">

                <img style={{boxShadow: "none"}} onClick={this.left} className="btnLeft" src="./SVG/left.svg" alt="" />

            {this.state.data.map(e =>
                <Home
                key={e.id}
                id={e.id}
                title={e.title}
                img={e.poster_path}
                voting={e.vote_average}
                release_date={e.release_date}
                />


                )}

            <img style={{boxShadow: "none"}} onClick={this.right} className="btnRight" src="./SVG/right.svg" alt="" />

            </div>
            </div>
         );
    }
}

export default HomeFetch;
